const sizeToDimensions = ({ size, axis }: Props): Dimensions => {
  const [width, height] = size
    ? axis === "width"
      ? [size, size / 4]
      : axis === "height"
        ? [size * 4, size]
        : [size, size]
    : [200, 50];

  return {
    width,
    height,
  };
};

